import React from 'react';
import './App.css';
import DogSpan from './DogSpan';
import BigDogContainer from './BigDogContainer';

class App extends React.Component {

  state = {
    allDogs: [],
    selectedDog: {}
  }

  componentDidMount(){
    fetch('http://localhost:3000/pups')
    .then(res => res.json())
    .then(dogs => this.setState({
      allDogs: dogs
      }
    ))
  }

  clickDog = (dog) => {
    this.setState({
      selectedDog: dog
    })
  }


  render() {
    return (
      <div className="App">
        <div id="filter-div">
          <button id="good-dog-filter">Filter good dogs: OFF</button>
        </div>
        <div id="dog-bar">
          {this.state.allDogs.map(dog => <DogSpan dog={dog} handleClick={this.clickDog}/>)}
        </div>
        <div id="dog-summary-container">
          <h1>DOGGO:</h1>
          <div id="dog-info">
            <img src={this.state.selectedDog.image}></img>
            <h2>{this.state.selectedDog.name}</h2>
            <button>{this.state.selectedDog.isGoodDog ? "GOOD DOG!" : "BAD DOG!" }</button>  
          </div>
        </div>
      </div>
    );
  }
}

export default App;
